import React, { useEffect , useState } from "react";
import QRCode from 'qrcode.react';




function App() {
  const [name, setName] = useState("");
  const [headingText, setHeading] = useState("");
  const [location, setLocation] = useState("");
  const[maps,setMaps] = useState("");
  const[page,setPage] = useState(1);

  function set(){
    if(page===2 ) return
    setPage((page) => page+1);
  }





  function handleChange(event) {
    console.log(event.target.value);
    setName(event.target.value);
  }


  function handleClick(event) {
    setHeading(name);
    navigator.geolocation.getCurrentPosition((position) => {

      setLocation("Your Location is :" + position.coords.latitude +" and " + position.coords.longitude);
      var link = "https://maps.googleapis.com/maps/api/staticmap?center=40.714728,-73.998672&zoom=12&size=400x400&maptype=roadmap&key=AIzaSyA8d7doPQVJEe_-cMIflaI7wXdGVkwashY"
      setMaps(link);
    });





    event.preventDefault();
  }



  return (
    <div className="container">
      <h1>Hello {headingText}</h1>
      <h2>{location}</h2>
      <h3>
        <img src={maps}></img>

      </h3>
      {page===1 && <showQRCode/>}

      <form onSubmit={handleClick}>
        <input
          onChange={handleChange}
          type="text"
          placeholder="What's your name?"
          value={name}
        />
        <button type="submit" onClick={set}>Submit</button>
      </form>
    </div>
  );
}
function showQRCode(){
  return(
    <div>
      <QRCode id="123" value="abc"/>
    </div>
  )
}


export default App;
